import React, { useEffect, useState } from "react";
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const API = 'http://localhost:8000/'

const App = () => {
  const [countryNames, setCountryNames] = useState<Array<string>>([])
  const [selectedCountry, setSelectedCountry] = useState<string | null>(null)
  const [countries, setCountries] = useState([])

  const getUsers = async () => {
    const res = await axios.get(API)
    return res.data
  }

  const setDefaultData = async () => {
    const data: any = await getUsers()
    console.log('res', data)
    const countriesArray = data.countries.map((country: any) => country.name)
    setSelectedCountry(countriesArray[0])
    setCountries(data.countries)
    setCountryNames(countriesArray)
  }

  const usersFromSelectedCountry = () => {
    // const userData: any = users.find((data: any) => data.country === selectedCountry)
    // return userData?.users
  }

  console.log('usersFromSelectedCountry', usersFromSelectedCountry())

  useEffect(() => {
    setDefaultData()
  }, [])

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between w-500">
        <div>
          {selectedCountry && (
            <Form.Select value={selectedCountry}>
             {countryNames.map((countries) => (
                <option key={countries} value={countries}>{countries}</option>
             ))}
           </Form.Select>
          )}
        </div>
        <Button>Reload</Button>
      </div>
      {

      }
    </Container>
  );
};

export default App;
