import React, { useEffect, useMemo, useState } from "react";
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const API = 'http://localhost:8000/'

type User = {
  gender: string
  name: string
  country: string
  email: string
}

type Users = Array<User>

type Country = {
  name: string
  users: Users
}

type Countries = Array<Country>

const App = () => {
  const [countryNames, setCountryNames] = useState<Array<string>>([])
  const [selectedCountry, setSelectedCountry] = useState<string | null>(null)
  const [countries, setCountries] = useState<Countries>([])

  const getUsers = async () => {
    const res = await axios.get<{ countries: Countries }>(API)
    return res.data
  }

  const setDefaultData = async () => {
    const data = await getUsers()
    console.log('res', data)
    const countriesArray = data.countries.map((country) => country.name)
    setSelectedCountry(countriesArray[0])
    setCountries(data.countries)
    setCountryNames(countriesArray)
  }

  const users = useMemo(() => {
    const currentCountry = countries.find((data) => data.name === selectedCountry)
    if (currentCountry) {
      return currentCountry.users
    }

    return []
  }, [countries, selectedCountry])

  const changeHandler = (e: any) => {
    setSelectedCountry(e.target.value)
  }

  useEffect(() => {
    setDefaultData()
  }, [])

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between w-500">
        <div>
          {selectedCountry && (
            <Form.Select value="3" onChange={changeHandler}>
              {countryNames.map((countries) => (
                <option key={countries} value={countries}>{countries}</option>
              ))}
           </Form.Select>
          )}
        </div>
        <Button>Reload</Button>
      </div>
      {
        users.ma
      }
    </Container>
  );
};

export default App;
